import { lego } from '@armathai/lego';
import { Howl } from 'howler';

import { BoardEvents, KeyboardEvents } from './events/MainEvents';
import { GameModelEvents } from './events/ModelEvents';
import { GameState } from './models/GameModel';

const click =
    'data:audio/mpeg;base64,';

const correctWord =
    'data:audio/mpeg;base64,';

const correctCard =
    'data:audio/mpeg;base64,';

const wrong =
    'data:audio/mpeg;base64,';
class SoundControl {
    private sounds: any;
    public constructor() {
        this.sounds = {};
        lego.event
            .on(KeyboardEvents.KeyClicked, this.playClick, this)
            .on(BoardEvents.CardClick, this.playClick, this)
            .on(GameModelEvents.StateUpdate, this.onGameStateUpdate, this);
    }

    public loadSounds(): void {
        this.sounds.click = new Howl({ src: click });
        this.sounds.wrong = new Howl({ src: wrong });
        this.sounds.correctWord = new Howl({ src: correctWord });
        this.sounds.correctCard = new Howl({ src: correctCard });
    }

    private playClick(): void {
        this.sounds.click.play();
    }

    private onGameStateUpdate(state: GameState): void {
        switch (state) {
            case GameState.RightAnswer:
                this.sounds.correctWord.play();
                break;
            case GameState.WrongAnswer:
                this.sounds.wrong.play();
                break;
            case GameState.Completed:
                this.sounds.correctCard.play();
                break;

            default:
                break;
        }
    }
}

const SoundController = new SoundControl();
export default SoundController;
